-- ----------------------------
-- Table structure for t_book_index
-- ----------------------------
DROP TABLE IF EXISTS `t_book_index`;
CREATE TABLE `t_book_index`  (
  `id` bigint(10) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `create_time` timestamp(0) NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` timestamp(0) NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '修改时间',
  `book_id` int(10) UNSIGNED NOT NULL COMMENT '小说ID',
  `word_count` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '章节字数',
  `title` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '章节名',
  `status` tinyint(3) UNSIGNED NOT NULL DEFAULT 1 COMMENT '状态:0草稿，1发布',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `unq_bookid_title`(`book_id`, `title`) USING BTREE,
  INDEX `idx_bookid_utime`(`book_id`, `update_time`) USING BTREE,
  INDEX `idx_status`(`status`) USING BTREE,
