-- ----------------------------
-- Table structure for t_book_setting
-- ----------------------------
DROP TABLE IF EXISTS `t_book_setting`;
CREATE TABLE `t_book_setting`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `create_time` timestamp(0) NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` timestamp(0) NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '修改时间',
  `book_id` int(10) UNSIGNED NOT NULL COMMENT '小说ID',
  `type` tinyint(3) UNSIGNED NOT NULL COMMENT '类型，1：轮播图，2：本周强推，3：热门推荐，4：精品推荐',
  `sort` tinyint(3) UNSIGNED NULL DEFAULT NULL COMMENT '排序号',
  PRIMARY KEY (`id`) USING BTREE,
  CONSTRAINT `fk_setting_book` FOREIGN KEY (`book_id`) REFERENCES `t_book` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 23 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '首页小说推荐表' ROW_FORMAT = Compact;

